mapper.select:
SELECT T_ARTICLE.id, T_ARTICLE.title, T_ARTICLE.tag, T_ARTICLE.create_by, T_ARTICLE.update_by, T_ARTICLE.IS_RELEASE, T_ARTICLE.create_time, T_ARTICLE.update_time, T_ARTICLE.ORDER_NUM FROM T_ARTICLE WHERE T_ARTICLE.IS_DELETE = 0
AND T_ARTICLE.title LIKE #{title}
ORDER BY T_ARTICLE.ORDER_NUM DESC, T_ARTICLE.create_time DESC

select id,TITLE,CREATE_TIME,TAG from T_ARTICLE where IS_DELETE=0 and IS_RELEASE=1
and T_ARTICLE.tag=#{code}
ORDER BY CREATE_TIME DESC
